prompt --application/shared_components/plugins/dynamic_action/scheffer_excel2apex
begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(4792863507309390364)
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'SCHEFFER.EXCEL2APEX'
,p_display_name=>'Excel2APEX'
,p_category=>'MISC'
,p_supported_ui_types=>'DESKTOP'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('DYNAMIC ACTION','SCHEFFER.EXCEL2APEX'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'procedure load_ddl( p_plugin_name in varchar2, p_file_name in varchar2 )',
'is',
'  l_ddl blob;',
'  l_src clob;',
'  l_ddl_tab dbms_sql.varchar2a;',
'  l_cur integer;',
'  l_start number := 1;',
'  l_pos number;',
'  src_offset number := 1;',
'  dest_offset number := 1;',
'  lang_context number := dbms_lob.default_lang_ctx;',
'  warning number;',
'  l_cr constant varchar2(1) := chr(13);',
'  l_lf constant varchar2(1) := chr(10);',
'begin',
'    select pgf.file_content',
'    into l_ddl',
'    from apex_appl_plugin_files pgf',
'    where pgf.plugin_name = p_plugin_name',
'    and   pgf.application_id = apex_application.g_flow_id',
'    and   pgf.file_name = p_file_name;',
'--',
'    if l_ddl is not null and dbms_lob.getlength( l_ddl ) > 0',
'    then',
'      dbms_lob.createtemporary( l_src, true, dbms_lob.call );',
'      dbms_lob.converttoclob( l_src, l_ddl, dbms_lob.lobmaxsize, dest_offset, src_offset, dbms_lob.default_csid , lang_context, warning );',
'--',
'      for i in 1 .. 25000',
'      loop',
'        l_pos := instr( l_src, l_cr, l_start );',
'        if l_pos > 0',
'        then',
'          l_ddl_tab( i ) := ltrim( substr( l_src, l_start, l_pos - l_start ), l_lf );',
'          l_start := l_pos + 1;',
'        else',
'          l_ddl_tab( i ) := ltrim( substr( l_src, l_start ), l_lf );',
'          exit;',
'        end if;',
'      end loop;',
'      dbms_lob.freetemporary( l_src );',
'--',
'      l_cur := dbms_sql.open_cursor;',
'      dbms_sql.parse( l_cur, l_ddl_tab, 1, l_ddl_tab.count, true, dbms_sql.native );',
'      dbms_sql.close_cursor( l_cur );',
'--',
'    end if;',
'end;',
'',
'function wrap_render',
'  ( p_dynamic_action in apex_plugin.t_dynamic_action',
'  , p_plugin         in apex_plugin.t_plugin',
'  )',
'return apex_plugin.t_dynamic_action_render_result',
'is',
'  l_render_result apex_plugin.t_dynamic_action_render_result;',
'  l_owner   varchar2(100);',
'  l_version varchar2(100);',
'  cursor c_aap( cp_app_id number )',
'  is',
'    select aap.version_identifier',
'    from apex_appl_plugins aap',
'    where aap.application_id = cp_app_id',
'    and   aap.name = p_plugin.name;',
'  r_aap c_aap%rowtype;',
'begin',
'  open c_aap( apex_application.g_flow_id );',
'  fetch c_aap into r_aap;',
'  close c_aap;',
'--',
'  l_owner := substr( apex_application.g_flow_owner, 1, 100 );',
'  l_version := apex_util.get_preference( p_plugin.name || ''_version'', l_owner );',
'--',
'  if r_aap.version_identifier > nvl( l_version, ''0.000'' )',
'  then',
'    load_ddl( p_plugin.name, ''as_excel2apex.pks'' );',
'    load_ddl( p_plugin.name, ''as_excel2apex.pkb'' );',
'      apex_util.set_preference( p_plugin.name || ''_version'', r_aap.version_identifier, l_owner );',
'  end if;',
'--',
'  apex_css.add( coalesce( p_dynamic_action.attribute_04, p_plugin.attribute_01 ) );',
'  apex_javascript.add_library( p_name      => ''excel2apex''',
'                             , p_directory => p_plugin.file_prefix',
'                             , p_version => null',
'                             );',
'  l_render_result.javascript_function := ''',
'  function()',
'  {',
'    jsv'' || p_dynamic_action.id || '' = anton.excel2apex.init( "'' || apex_plugin.get_ajax_identifier || ''"'' ||',
'       '', this.action );',
'  }'';',
'  return l_render_result;',
'end;',
''))
,p_api_version=>1
,p_render_function=>'wrap_render'
,p_ajax_function=>'as_excel2apex.ajax'
,p_standard_attributes=>'ITEM:JQUERY_SELECTOR'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'0.800'
